Enfoque Top-Down:

#include <iostream>
#include <vector>

using namespace std;

vector<int> memo(100, -1);

int fib(int n) {
    if (memo[n] != -1) {
        return memo[n];
    }
    if (n <= 1) {
        memo[n] = n;
    } else {
        memo[n] = fib(n-1) + fib(n-2);
    }
    return memo[n];
}

